import { Component } from "react";
import img from "./listformat.png"
import completedimg from "./check.png"
export class Todolist extends Component{
    state={
        todos: [],
        userInput:"",
        image: img
    }

    inputhandles(e){
        this.setState({userInput: e})
    }

    addTask(userTask){
        if (userTask === "") alert("Please enter a task")
        else{
            let taskArray = this.state.todos;
            taskArray.push(userTask);
            this.setState({todos:taskArray, userInput:""})
        }
    }

    changeOnChecked(event){
        const listItem = event.target;
        listItem.classList.toggle('crossed')
    }

    updatePicture(){
        this.setState({image: completedimg})
    }

    preventDefault(e){
        e.preventDefault()
    }

    // taskEdit(userInput){

    // }

    deleteAllItems(){
        this.setState({
            todos:[]
        })
    }

    render(){
        return(
            <div>
                <div>
                <div>
                <h2>To Do List</h2>
                <form onSubmit={this.preventDefault}>
                    <input type="text" placeholder="Enter your task..." onChange={(e)=>this.inputhandles(e.target.value)} value={this.state.userInput}/>
                    <button onClick={()=>this.addTask(this.state.userInput)}>ADD</button>
                </form>
                </div>

                <div className="list-task">
                    {this.state.todos.map((item, index) =>
                    <li className="listItems" key={index} onClick={this.changeOnChecked}>      
                    <div className="item-content">
                    <img className="item-image" src={this.state.image} alt="item" />
                    <span className="item-text">{item}</span>
                    </div>
                    </li>)}
                </div>
                <button onClick={()=>this.deleteAllItems()}>delete all</button>
            </div>
            </div>
        )
    }
}
