import { Component } from "react";

export class Todolist extends Component{
    state={
        todos: [],
        userInput:""
    }

    inputhandles(e){
        this.setState({userInput: e})
    }

    addTask(userTask){
        if (userTask === "") alert("Please enter a task")
        else{
            let taskArray = this.state.todos;
            taskArray.push(userTask);
            this.setState({todos:taskArray, userInput:""})
        }
    }

    taskDone(){
        e.target.closest('li').style.color='grey'
    }
    preventDefault(e){
        e.preventDefault()
    }


    render(){
        return(
            <div>
                <h2>To Do List</h2>
                <form onSubmit={this.preventDefault}>
                    <input type="text" placeholder="Enter your task..." onChange={(e)=>this.inputhandles(e.target.value)}/>
                    <button onClick={()=>this.addTask(this.state.userInput)}>ADD</button>
                </form>

                <ul>
                    {this.state.todos.map((item, index) =>
                    <li onClick={()=>this.taskDone()} key={index}><input type="checkbox"/>{item}</li>)}
                </ul>
            </div>
        )
    }
}
