import { Component } from "react";

export class Todolist extends Component{
    state={
        todos: [],
        userInput:""
    }

    inputhandles(e){
        this.setState({userInput: e})
    }
    addTask(userTask){
        if (userTask === "") alert("Please enter a task")
        else{
            let taskArray = this.state.todos;
            taskArray.push(userTask);
            this.setState({todos:taskArray, userInput:""})
        }
    }

    render(){
        return(
            <div>
                <h2>To Do List</h2>
                <form>
                    <input type="text" placeholder="Enter your task..." onChange={(e)=>this.inputhandles(e.target.value)}/>
                    <button onClick={()=>addTask}></button>
                </form>
            </div>
        )
    }
}
