import { Component } from "react";

export class Todolist extends Component{
    state={
        todos: [],
        userInput:""
    }

    inputhandles(e){
        this.setState({userInput: e})
    }

    addTask(userTask){
        if (userTask === "") alert("Please enter a task")
        else{
            let taskArray = this.state.todos;
            taskArray.push(userTask);
            this.setState({todos:taskArray, userInput:""})
        }
    }

    changeOnChecked(event){
        const listItem = event.target.closest('li');
        listItem.classList.toggles('crossed')
    }

    preventDefault(e){
        e.preventDefault()
    }


    render(){
        return(
            <div>
                <h2>To Do List</h2>
                <form onSubmit={this.preventDefault}>
                    <input type="text" placeholder="Enter your task..." onChange={(e)=>this.inputhandles(e.target.value)} value={this.state.userInput}/>
                    <button onClick={()=>this.addTask(this.state.userInput)}>ADD</button>
                </form>


                    {this.state.todos.map((item, index) =>
                    <li type="" key={index}><input type="checkbox" onClick={(event)=>this.changeOnChecked(event)} />{item}</li>)}

            </div>
        )
    }
}
