import { Component } from "react";

export class Todolist extends Component{
    state = {
        userinput: "",
        task: []
    }
    //input is being tracked
    handleChange(e){
        this.setState({userInput: e})
    }


    addTask(task){
        let taskList = this.state.task;
        taskList.push(task)
        this.setState({task : taskList})
    }

    render(){
        return(
            <div>
                <h1>TO DO LIST</h1>
                <form onSubmit={this.addTask}> 
                <input type="text" placeholder="Enter your plans" onChange={(e)=>this.handleChange(e.target.value)} value={this.state.userinput}/> 
                <button>ADD</button>
                </form>

                <div>
                </div>
            </div>

        );
    }
}
