import { Component } from "react";

export class Todolist extends Component{
    state = {
        userinput: "",
        tasks: []
    }
    handleChange(e){
        this.setState({userinput: e})
    }
    preventDefault(event){
        event.preventDefault();
    }
    addTask(task){
        if (task===''){
            alert("Enter a task...")
        }else{
        let taskList = this.state.tasks;
        taskList.push(task)
        this.setState({tasks : taskList, userinput:""})
        }
    }

    render(){
        return(
            <div>
                <h1>TO DO LIST</h1>
                <form onSubmit={this.preventDefault}> 
                <input type="text" placeholder="Enter your plans" onChange={(e)=>{this.handleChange(e.target.value)}} value={this.state.userinput}/> 
                <button onClick={()=>this.addTask(this.state.userinput)}>ADD</button>
                </form>

                <div>
                    <ul>
                        {this.state.tasks.map((item, index)=>
                        <li key={index}>{item}</li>)}
                    </ul>
                </div>
            </div>

        );
    }
}
