import { Component } from "react";

export class Todolist extends Component{
    state = {
        date: " ",
        task: []
    }
    //input is being tracked
    handleChange(e){
        console.log(e)
    }


    addTask(task,date){
        let taskList = this.state.task;
        taskList.push(task)
        this.setState({task : taskList, date: new Date()})

    }

    render(){
        return(
            <div>
                <h1>TO DO LIST</h1>
                <form onSubmit={this.addTask}> 
                <input type="text" placeholder="Enter your plans" onChange={(e)=>this.handleChange(e.target.value)} value={this.state.task}/> 
                <input type="date" onChange={(e)=>this.handleChange()}/>
                <button>ADD</button>
                </form>

                <div>
                    <h2>{this.state.date} April 1, 2024</h2>
                    <input type="text" placeholder="Enter your plans"/>
                    <button>ADD</button>
                    <div>
                    <input type="checkbox" />Done?
                    <button>Edit</button>
                    </div>
                </div>
            </div>

        );
    }
}
